before = getSortsbefore(di, dj);
	if (!before) {
tmp = array[i];
		array[i] = (array[j]);
		array[j] = tmp;
		tt = di;
		di = dj;
		dj = tt;
	}
	if (n <= 2) {
return 0;
	}

	/* ij is the midpoint of i and j. */
ij = (i + j) / 2;

	/* Sort di,dij,dj.  Make dij be their median. */
dij = array[ij];

	/* i.e. should di precede dij? */
