/*	The heart of performance counting in Sista. Conditional branches are 6
	times less
	frequent than sends and can provide basic block frequencies (send counters
	can't). Each conditional has a 32-bit counter split into an upper 16 bits
	counting executions
	and a lower half counting untaken executions of the branch. Executing the
	branch decrements the upper half, tripping if the count goes negative. Not
	taking the branch
	decrements the lower half. N.B. We *do not* eliminate dead branches (true
	ifTrue:/true ifFalse:)
	so that scanning for send and branch data is simplified and that branch
	data is correct. */

static sqInt
genJumpIfto(sqInt boolean, sqInt targetBytecodePC)
{
    AbstractInstruction *abstractInstruction;
    AbstractInstruction *counter;
    AbstractInstruction *countTripped;
    CogSimStackEntry *desc;
    void *jumpTarget;
    AbstractInstruction *ok;
    sqInt quickConstant;
    AbstractInstruction *retry;

	ssFlushTo(simStackPtr - 1);
	desc = ssTop();
	ssPop(1);
	popToReg(desc, TempReg);
	ssAllocateRequiredReg(SendNumArgsReg);
	counter = (&(counters[counterIndex]));
	counterIndex += 1;
	flag("will need to use MoveAw32:R: if 64 bits");
	assert(BytesPerWord == CounterBytes);
	/* begin MoveAw:R: */
	retry = genoperandoperand(MoveAwR, ((usqInt)counter), SendNumArgsReg);
	addDependent(counter, annotateAbsolutePCRef(retry));
	/* begin SubCq:R: */
	genoperandoperand(SubCqR, 65536, SendNumArgsReg);
	/* begin JumpCarry: */
	countTripped = genoperand(JumpCarry, ((sqInt)0));
	addDependent(counter, annotateAbsolutePCRef(gMoveRAw(SendNumArgsReg, ((usqInt)counter))));
	assert((objectAfter(falseObject())) == (trueObject()));
	annotateobjRef(gSubCwR(boolean, TempReg), boolean);
	/* begin JumpZero: */
	jumpTarget = ensureFixupAt(targetBytecodePC - initialPC);
	genoperand(JumpZero, ((sqInt)jumpTarget));
	/* begin SubCq:R: */
	genoperandoperand(SubCqR, 1, SendNumArgsReg);
	addDependent(counter, annotateAbsolutePCRef(gMoveRAw(SendNumArgsReg, ((usqInt)counter))));
	/* begin CmpCq:R: */
	quickConstant = (boolean == (falseObject())
		? (trueObject()) - (falseObject())
		: (falseObject()) - (trueObject()));
	genoperandoperand(CmpCqR, quickConstant, TempReg);
	/* begin JumpZero: */
	ok = genoperand(JumpZero, ((sqInt)0));
	/* begin MoveCq:R: */
	genoperandoperand(MoveCqR, 0, SendNumArgsReg);
	jmpTarget(countTripped, CallRT((boolean == (falseObject())
		? ceSendMustBeBooleanAddFalseTrampoline
		: ceSendMustBeBooleanAddTrueTrampoline)));
	/* begin annotateBytecode: */
	/* begin Label */
	abstractInstruction = genoperandoperand(Label, (labelCounter += 1), bytecodePC);
	annotatewith(abstractInstruction, HasBytecodePC);
	/* begin Jump: */
	genoperand(Jump, ((sqInt)retry));
	jmpTarget(ok, gLabel());
	return 0;
}

