	/* BalloonEnginePlugin>>#repeatValue:max: */
static sqInt
repeatValuemax(sqInt delta, sqInt maxValue)
{
    sqInt newDelta;

	newDelta = delta;
	while (newDelta < 0) {
		newDelta += maxValue;
	}
	while (newDelta >= maxValue) {
		newDelta -= maxValue;
	}
	return newDelta;
}

