		workBuffer[(workBuffer[GWBufferTop])] = (workBuffer[(workBuffer[GWBufferTop]) + (((workBuffer[GWSize]) - (workBuffer[GWBufferTop])) - 3 /* begin stackFillEntryLength */)]);
		workBuffer[(workBuffer[GWBufferTop]) + (1)] = (workBuffer[(workBuffer[GWBufferTop]) + ((((workBuffer[GWSize]) - (workBuffer[GWBufferTop])) - 3 /* begin stackFillEntryLength */) + 1)]);
		workBuffer[(workBuffer[GWBufferTop]) + (2)] = (workBuffer[(workBuffer[GWBufferTop]) + ((((workBuffer[GWSize]) - (workBuffer[GWBufferTop])) - 3 /* begin stackFillEntryLength */) + 2)]);
		workBuffer[(workBuffer[GWBufferTop]) + (((workBuffer[GWSize]) - (workBuffer[GWBufferTop])) - 3 /* begin stackFillEntryLength */)] = fillIndex;
