primitiveCanReceiveSignals(void)
{
    pid_t pidToSignal;
    int result;

	if (isIntegerObject(stackValue(0))) {
		pidToSignal = stackIntegerValue(0);
		result = kill(pidToSignal, 0);
		pop(2);
		if (result == 0) {
			push(trueObject());
		}
		else {
			push(falseObject());
		}
	}
	else {
		pop(2);
		push(falseObject());
	}
	return 0;
}


