	cacheValue = selector;
	/* begin MoveUniqueC32:R: */
	opcode = MoveCwR;
	/* begin uniqueLiteral32:forInstruction: */
	anInstruction = genoperandoperand(opcode, cacheValue, ClassReg);
	assert(usesOutOfLineLiteral(anInstruction));
	/* begin allocateLiteral: */
