	next = ((((usqInt)(newFreeChunk + sz3))) >= GIV(freeStart)
		? ((sqInt)GIV(freeStart))
		: (newFreeChunk + sz3) + (headerTypeBytes[(longAt(newFreeChunk + sz3)) & TypeMask]));
